import {useEffect, useState} from 'react';

import {Articulos} from './Item'
import { ItemList } from "./ItemList";
import React from "react";
import { useParams } from 'react-router-dom';

export const ItemListContainer = () => {

  const [item, setItems] = useState([]) 

  const { type} = useParams();



  useEffect(() => {

    const obtenerProducto = async (type) => {
      const responsePromise = await obtenerProductoBD(type);
      setItems(responsePromise);

    }
    obtenerProducto(type);
  }, [type])


  const obtenerProductoBD = (nombreProducto) => {
    return new Promise((resolve, reject) => {

      const arregloProductosBD = Articulos;
      const productoEncontrado = arregloProductosBD.filter((elemento) => elemento.type === nombreProducto);


      console.log("producto encontrado", productoEncontrado)

      setTimeout(() => {
        resolve(productoEncontrado)
      });

    })

  }


  return (

    Articulos.map(producto => {

      return (


          <div className=" cardPadre">
            <ItemList
              productoProp={producto}
            />
          </div>

      )
    })
  )

};
