import "./Cart.css";

import { Link } from "react-router-dom";
import React from 'react';
import { useCartContext } from "../Context/CartContext";

export const Cart = () => {
    const { cartItems } = useCartContext();

 return (
        <>
            {cartItems.length === 0 ? (

                <div> <p>actualmente el carrito est√° vacio</p>
                <Link to={"/category"} >  <button className="cartEmptyButton"> Agregar productos </button> </Link>
     </div>

            ) : (

                cartItems.map((i) => {
                    return (

                        <div>
                            <table>

                                <tr className='cartHijo'>
                                    <th>
                                        <img alt='producto' src={i.img} style={{ width: 100 }} />
                                    </th>
                                    <th>
                                        {i.title}
                                    </th>
                                    <th>{i.price}</th>
                                    <th>
                                        {i.amount}
                                    </th>
                                    <th>
                                        ${i.amount * i.price}
                                    </th>
                                    <th>
                                        <p id="totalCarrito"> X</p>
                                    </th>

                                </tr>


                            </table>

                        </div>


                    );
                })
            )}
        </>
    );
};


