import "../Cart/Cart.css"

import { Cart } from "./Cart"
import { Link } from "react-router-dom";
import { useCartContext } from "../Context/CartContext";

export const CartContainer = () => {

    const { cartItems } = useCartContext();


    return (
        <>
            {cartItems.length === 0 ? (

                <div className="carritoVacio">
                    <p>actualmente el carrito est√° vacio</p>
                    <Link to={"/category"} >  <button className="cartEmptyButton"> Agregar productos </button> </Link>
                </div>

            ) : (

                cartItems.map((i) => {
                    return (
                        <div >
                            <table className="cartPadre ">
                                <thead className="cartHijo">
                                    <tr>
                                        <th>Imagen</th>
                                        <th>Nombre</th>
                                        <th> Precio </th>
                                        <th> Cantidad</th>
                                        <th>subtotal</th>
                                        <th>Eliminar</th>
                                    </tr>
                                </thead>

                                <tbody className="cartHijo">
                                    <Cart ></Cart>
                                </tbody>

                                <tbody>
                                    <tr>

                                        <td>
                                            <div className="cartLine"></div>

                                        </td>
                                    </tr>

                                </tbody>

                                <tfoot>

                                    <tr>



                                        <td> total: ${i.amount * i.price}

                                        </td>
                                    </tr>

                                </tfoot>
                            </table>

                        </div>
                    )
                })
                )}
        </>
    );
};
