import "./Cart.css";

import React from 'react';
import { useCartContext } from "../Context/CartContext";

export const Cart = () => {
    const { cartItems } = useCartContext();

 return (
        <>
            {cartItems.length === 0 ? (

                <div></div>

            ) : (

                cartItems.map((i) => {
                    return (

                        <div className='cartHijo'>


                                <tr >
                                    <th>
                                        <img alt='producto' src={i.img} style={{ width: 100 }} />
                                    </th>
                                    <th>
                                        {i.title}
                                    </th>
                                    <th>{i.price}</th>
                                    <th>
                                        {i.amount}
                                    </th>
                                    <th>
                                        ${i.amount * i.price}
                                    </th>
                                    <th>
                                        <button className="deleteButton">
                                        <p id="totalCarrito">X</p>
                                        </button>

                                    </th>

                                </tr>




                        </div>


                    );
                })
            )}
        </>
    );
};


